import logo from "./logo.svg";
import "./App.css";
import Web3 from "web3";
import { useState, useEffect } from "react";
import calc from "./contracts/calculator.json";
import { toBN } from "web3-utils";

function App() {
  const [account, setAccount] = useState(0);
  const [val, setVal] = useState(null);
  const [a, setA] = useState("");
  const [b, setB] = useState("");
  const [addresult, setAddResult] = useState("");

  const contract_address = calc.networks["5777"].address;

  const contract_abi = calc.abi;
  // const parsecontract = JSON.parse(JSON.stringify(contract_abi))

  const HttpProvider = "http://127.0.0.1:7545";
  // const web3 = new Web3(Web3.givenProvider || "http://127.0.0.1:7545");
  const web3 = new Web3(new Web3.providers.HttpProvider(HttpProvider));
  const contractData = new web3.eth.Contract(contract_abi, contract_address);

  let accountmeta = 0;
  const main = async () => {
    const accounts = await web3.eth.getAccounts();
    accountmeta = accounts[0];
    setAccount(accountmeta);
    console.log(account);
  };

  // const getcontract = async() => {
  //   await contractData.methods.gettingdata().call().then(result => {

  //     setData(result);
  //     console.log(result);

  //   });
  //  // console.log(getval);
  // };
  const getcontract = async () => {
    // await contractData.methods
    //   .gettingdata()
    //   .call()
    //   .then((result) => {
    //     console.log(result);
    //     setData(result);
    //   })
    //   .catch((error) => {
    //     console.error("Error fetching data:", error);
    //   });

    const resultBN = await contractData.methods.gettingdata().call();
    const result = resultBN.toString();
    console.log(result);
    setVal(result);
  };

  //const Web3 = require("web3");
  const ethEnabled = async () => {
    if (window.ethereum) {
      await window.ethereum.request({ method: "eth_requestAccounts" });
      window.web3 = new Web3(window.ethereum);
      console.log("metamask exist");
      return true;
    }
    console.log("metamask not exist");
    return false;
  };

  const addHandler = async () => {
    await contractData.methods.add(a, b).send({ from: account });
    const res = await contractData.methods.showresult().call();
    console.log(res);
    setAddResult(res.toString());

    setA("");
    setB("");
  };

  useEffect(() => {
    main();
    ethEnabled();
    // getcontract();
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        {account}
        <div>data: {val}</div>
        <button onClick={getcontract}>get data</button>
        <input
          type="number"
          value={a}
          onChange={(e) => setA((e.target.value))}
        ></input>
        <input
          type="number"
          value={b}
          onChange={(e) => setB((e.target.value))}
        ></input>
        <button onClick={addHandler}>add</button>
        <span>addition is {addresult} </span>
      </header>
    </div>
  );
}

export default App;
