import logo from "./logo.svg";
import "./App.css";
import Web3 from "web3";
import { useState, useEffect } from "react";
import calc from "./contracts/calculator.json";

function App() {
  const [account, setAccount] = useState([]);
  const [data, setData] = useState([]);

  const contract_address = calc.networks["5777"].address;

  const contract_abi = calc.abi;
  // const parsecontract = JSON.parse(JSON.stringify(contract_abi))

  const HttpProvider = "http://127.0.0.1:7545";
  // const web3 = new Web3(Web3.givenProvider || "http://127.0.0.1:7545");
  const web3 = new Web3(new Web3.providers.HttpProvider(HttpProvider));
  const contractData = new web3.eth.Contract(contract_abi, contract_address);

  let accountmeta = 0;
  const main = async () => {
    const accounts = await web3.eth.getAccounts();
    accountmeta = accounts[0];
    setAccount(accountmeta);
    console.log(account);
  };

  // const getcontract = async() => {
  //   await contractData.methods.gettingdata().call().then(result => {

  //     setData(result);
  //     console.log(result);

  //   });
  //  // console.log(getval);
  // };
  const getcontract = async () => {
    // await contractData.methods
    //   .gettingdata()
    //   .call()
    //   .then((result) => {
    //     console.log(result);
    //     setData(result);
    //   })
    //   .catch((error) => {
    //     console.error("Error fetching data:", error);
    //   });

    const result = await contractData.methods.gettingdata().call();

    console.log(result);
    setData(result)
  };

  //const Web3 = require("web3");
  const ethEnabled = async () => {
    if (window.ethereum) {
      await window.ethereum.request({ method: "eth_requestAccounts" });
      window.web3 = new Web3(window.ethereum);
      console.log("metamask exist");
      return true;
    }
    console.log("metamask not exist");
    return false;
  };

  useEffect(() => {
    main();
    ethEnabled();
    // getcontract();
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        {account}
        <div>data: {data}</div>
        <button onClick={getcontract}>get data</button>
      </header>
    </div>
  );
}

export default App;
