import logo from "./logo.svg";
import "./App.css";
import Web3 from "web3";
import { useState, useEffect } from "react";
import calc from "./contracts/calc.json";

function App() {
  const [account, setAccount] = useState(0);
  const contract_address = calc.networks["5777"].address;

  const contract_abi = calc.abi;
  // const parsecontract = JSON.parse(JSON.stringify(contract_abi))

  const HttpProvider = "http://127.0.0.1:7545";
  // const web3 = new Web3(Web3.givenProvider || "http://127.0.0.1:7545");
  const web3 = new Web3(new Web3.providers.HttpProvider(HttpProvider));
  const contractData = new web3.eth.Contract(contract_abi, contract_address);

  let accountmeta = 0;
  const main = async () => {
    const accounts = await web3.eth.getAccounts();
    accountmeta = accounts[0];
    setAccount(accountmeta);
    console.log(account);
  };

  main();

  const btnhandler = () => {

    // Asking if metamask is already present or not
    if (window.ethereum) {

      // res[0] for fetching a first wallet
      window.ethereum
        .request({ method: "eth_requestAccounts" })
        .then((res) => accountChangeHandler(res[0]));
    } else {
      alert("install metamask extension!!");
    }
  };

  return (
    <div className="App">
      <header className="App-header">{account}</header>
    </div>
  );
}

export default App;
